import React, { Component } from 'react';
import { XmlEntities as Entities } from 'html-entities';
import _ from 'lodash';

class App extends Component {

  state = {
    loading: true,
    questions:[],
    selected:[false,false,false,false,false,false,false,false,false,false]
  };

  async componentDidMount() {
    const url = "https://opentdb.com/api.php?amount=10";
    const response = await fetch(url);
    const data = await response.json();
    this.setState({
      questions:data.results
    });
    console.log(this.state.questions);
  }


  setAnswer = (e) => {
    const index = e.target.name;
    const selected = [...this.state.selected];
    console.log(selected);
    if (this.state.questions[index].correct_answer === e.target.value) {
      selected[index] = true;
    } else {
      selected[index] = false;
    }

    this.setState({ selected });
  }

  submit = (e) => {
    e.preventDefault();
    const { selected } = this.state;

    let correct = selected.filter(value => value).length;
    console.log(correct);
  }

  render() { 
    const {questions}= this.state
    const entities = new Entities();
    const questionList = questions.map((question,index) => {
      const answers = [question.correct_answer, ...question.incorrect_answers];
      const shuffledAnswers = _.shuffle(answers);
      console.log(shuffledAnswers, question.correct_answer);
        return(
            <div key={index}>             
              <form onSubmit={this.submit}>
               <div>{index+1}.{entities.decode(question.question)}</div>
               {shuffledAnswers.length === 2 ? (
                  <div onChange={this.setAnswer.bind(this)}>
                    <label>
                      <input type="radio" 
                        value={shuffledAnswers[0]}
                        name={index}
                      />{entities.decode(shuffledAnswers[0])}
                    </label>
                    <label>
                      <input type="radio" 
                        value={shuffledAnswers[1]}
                        name={index}
                      />{entities.decode(shuffledAnswers[1])}
                    </label>
                  </div>  
                ) : (
                  <div onChange={this.setAnswer.bind(this)}>
                      <label>
                      <input type="radio" 
                        value={shuffledAnswers[0]}
                        name={index}
                      />{entities.decode(shuffledAnswers[0])}
                    </label>
                    <label>
                      <input type="radio" 
                        value={shuffledAnswers[1]}
                        name={index}
                      />{entities.decode(shuffledAnswers[1])}
                    </label>
                    <label>
                      <input type="radio" 
                        value={shuffledAnswers[2]}
                        name={index}
                      />{entities.decode(shuffledAnswers[2])}
                    </label>
                    <label>
                      <input type="radio" 
                        value={shuffledAnswers[3]}
                        name={index}
                      />{entities.decode(shuffledAnswers[3])}
                    </label>
                  </div> 
                )}
                <button type="submit">Submit</button>
              </form>

            </div>
          )
      })

      return(
        <div>
          {this.state.Loading || !this.state.questions ? (
            <div>Loading...</div>
            ) : (
              <div>
                {questionList}
              </div>
            )}        
        </div>
      )
    }
  }


export default App;
