import React, { Component } from 'react';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      questions: [],
      isLoaded:false,

    }
  }

  componentDidMount() {

    fetch('https://opentdb.com/api.php?amount=10')
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded:true,
          questions:json,
        })
      });
      console.log(this.state)
  }

  render() { 

      var { isLoaded,questions } = this.state;

      if(!isLoaded) {
        return <div>Loading...</div>;
      }

      else{
        return (
        <div className="App">
          Data has been loaded    
        </div>
      );

      }      
    }
  }


export default App;
